				select *
				from ( 
				        select rownum rn, a.*
				        from (
				                select    u.user_name, b.*
                                from      user_info u, bd_free b
                                where     u.user_id = b.user_id
                                and       bd_category like '이%'
                                order by  b.create_date desc
				            ) a
					 )
				where   rn >= #{start}
				and     rn <= #{end}

