mapper.select:
SELECT Count(*) 
FROM PRJ_BD_DATA 
WHERE  project_id = #{project_id}

SELECT Count(*) 
FROM   PRJ_BD_DATA 
WHERE  project_id = #{project_id} 
AND    doc_group = #{doc_group}

SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT p.*, u.user_name, (SELECT cate_name FROM CODE WHERE UPPER(table_name)='PRJ_BD_DATA' AND UPPER(field_name)='BD_CATEGORY' AND cate_code=p.bd_category) bd_category_name, (SELECT Count(*) FROM BD_DATA_COMT WHERE doc_no=p.doc_no AND project_id=p.project_id) comment_count FROM PRJ_BD_DATA p, USER_INFO u WHERE p.user_id = u.user_id ORDER BY doc_group DESC, doc_step) a WHERE a.project_id = #{project_id} AND
a.subject like '%' || #{keyword} || '%'
a.user_name like '%' || #{keyword} || '%'
a.bd_category_name like '%' || #{keyword} || '%'
a.doc_body like '%' || #{keyword} || '%'
) WHERE rn BETWEEN #{start} AND #{end}

SELECT * 
FROM CODE 
WHERE UPPER(table_name)=#{table_name} AND UPPER(field_name)=#{field_name}

mapper.insert:
SELECT NVL(MAX(doc_no),0)+1 FROM PRJ_BD_DATA WHERE project_id=#{project_id}
INSERT INTO PRJ_BD_DATA( DOC_NO, PROJECT_ID, APP_ID, USER_ID, CREATE_DATE, SUBJECT, NOTIFY_FLAG, BD_CATEGORY, DOC_BODY, ATTACH_NAME, ATTACH_PATH, BD_COUNT, GOOD_COUNT, DOC_GROUP, DOC_STEP, DOC_INDENT, ALARM_FLAG, PARENT_DOC_USER_ID, PARENT_DOC_NO ) VALUES( #{doc_no}, #{project_id}, #{app_id}, #{user_id}, #{create_date}, #{subject}, #{notify_flag}, #{bd_category}, #{doc_body}, #{attach_name}, #{attach_path}, #{bd_count}, #{good_count},
#{doc_group} #{doc_no} , #{doc_step}, #{doc_indent}, #{alarm_flag}, #{parent_doc_user_id},
#{parent_doc_no} null
)

mapper.update:
UPDATE PRJ_BD_DATA 
SET    BD_COUNT=BD_COUNT+1
WHERE  DOC_NO=#{doc_no} AND PROJECT_ID=#{project_id}

UPDATE PRJ_BD_DATA 
SET    GOOD_COUNT=(SELECT COUNT(*) FROM BD_DATA_GOOD WHERE doc_no=#{doc_no} AND project_id=#{project_id}) 
WHERE  DOC_NO=#{doc_no} AND PROJECT_ID=#{project_id}

mapper.select:
SELECT * 
FROM BD_DATA_GOOD 
WHERE doc_no=#{doc_no} AND project_id=#{project_id} AND user_id=#{user_id}

mapper.update:
UPDATE PRJ_BD_DATA 
SET doc_step=doc_step+1 
WHERE doc_group=#{doc_group} AND doc_step > #{doc_step}

UPDATE PRJ_BD_DATA 
SET ALARM_FLAG='Y' 
WHERE DOC_NO=#{doc_no} AND PROJECT_ID=#{project_id}

UPDATE BD_DATA_COMT 
SET ALARM_FLAG='Y' 
WHERE DOC_NO=#{doc_no} AND PROJECT_ID=#{project_id}
