mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT ai.app_name, ai.app_id, bdq.doc_no, bdq.subject, bdq.user_id, bdq.create_date, bdq.bd_count, bdq.good_count, NULL AS project_id, NULL AS bd_category FROM bd_qna bdq, app_info ai WHERE bdq.app_id = ai.app_id AND bdq.user_id = #{user_id}
UNION ALL
SELECT ai.app_name, ai.app_id, bdf.doc_no, bdf.subject, bdf.user_id, bdf.create_date, bdf.bd_count, bdf.good_count, NULL AS project_id, bdf.bd_category FROM bd_free bdf, app_info ai WHERE bdf.app_id = ai.app_id AND bdf.user_id = #{user_id}
UNION ALL
SELECT ai.app_name, ai.app_id, pbd.doc_no, pbd.subject, pbd.user_id, pbd.create_date, pbd.bd_count, pbd.good_count, pbd.project_id, NULL AS bd_category FROM prj_bd_data pbd, app_info ai WHERE pbd.app_id = ai.app_id AND pbd.user_id = #{user_id}
UNION ALL
SELECT ai.app_name, ai.app_id, pbr.doc_no, pbr.subject, pbr.user_id, pbr.create_date, NULL, NULL, pbr.project_id, NULL AS bd_category FROM prj_bd_rep pbr, app_info ai WHERE pbr.app_id = ai.app_id AND pbr.user_id = #{user_id} ORDER BY create_date DESC ) a ) WHERE rn >= #{start} AND rn <= #{end}

SELECT * FROM ( SELECT rownum rn, a.* FROM ( Select info.app_name, info.app_id, pdata.project_id, pdata.bd_category, pdata.doc_no, comt.user_id , comt.comment_context, comt.comment_doc_no, comt.create_date From app_info info, prj_bd_data pdata, bd_data_comt comt Where info.app_id = pdata.app_id And pdata.doc_no = comt.doc_no And comt.user_id = #{user_id}
UNION ALL
Select info.app_name, info.app_id, rep.project_id, rep.bd_category, rep.doc_no, comt.user_id , comt.comment_context, comt.comment_doc_no, comt.create_date From app_info info, prj_bd_rep rep, bd_rep_comt comt Where info.app_id = rep.app_id And rep.doc_no = comt.doc_no And comt.user_id = #{user_id}
UNION ALL
Select info.app_name, info.app_id, NULL as project_id, free.bd_category, free.doc_no, comt.user_id, comt.comment_context, comt.comment_doc_no, comt.create_date From app_info info, bd_free free, bd_free_comt comt Where info.app_id = free.app_id And free.doc_no = comt.doc_no And comt.user_id = #{user_id} ORDER BY create_date DESC ) a ) WHERE rn >= #{start} AND rn <= #{end}

SELECT * FROM ( SELECT rownum rn, a.* FROM ( select ai.app_name, ai.app_id, bdfg.user_id, bdfg.doc_no, bdf.subject, bdf.create_date, bdf.bd_count, bdf.good_count, NULL AS project_id, bdf.bd_category from bd_free_good bdfg, app_info ai, bd_free bdf where bdfg.doc_no = bdf.doc_no and ai.app_id = bdf.app_id And bdfg.user_id = #{user_id}
UNION ALL
select ai.app_name, ai.app_id, bdqg.user_id, bdqg.doc_no, bdq.subject,bdq.create_date, bdq.bd_count, bdq.good_count, NULL AS project_id, bdq.bd_category from bd_qna_good bdqg, app_info ai, bd_qna bdq where bdqg.doc_no = bdq.doc_no and ai.app_id = bdq.app_id And bdqg.user_id = #{user_id}
UNION ALL
select ai.app_name, ai.app_id, bddg.user_id, bddg.doc_no, pbd.subject, pbd.create_date, pbd.bd_count, pbd.good_count, pbd.project_id, pbd.bd_category from bd_data_good bddg, app_info ai, prj_bd_data pbd where bddg.doc_no = pbd.doc_no and ai.app_id = pbd.app_id And bddg.user_id = #{user_id} ORDER BY create_date DESC ) a ) WHERE rn >= #{start} AND rn <= #{end}
